observeEvent(input$Player, {
  base &lt;- PlayersData()
  base &lt;- base %&gt;% filter(player == input$Player)
  updateSelectInput(session, "Season", choices = unique(base$season))
  base &lt;- base %&gt;% filter(season == input$Season)
})

Players_Inputs &lt;- reactive({
  base &lt;- PlayersData()
  base &lt;- base %&gt;% filter(player == input$Player)
  base &lt;- base %&gt;% filter(season == input$Season)
  return(base)
})
